#NUMPY FOR HANDLING LARGE DATASETS, MATRIX OPERATIONS AND NUMERICAL COMPUTATIONSimport numpy as nparray_1d = np.array([1,2,3,4])print("1D Array: \n", array_1d)array_2d = np.array([[1,2], [3,4]])print("2D Array: \n", array_2d)array_addition = array_2d + 5print("Array after addition: ", array_addition)#PANDAS FOR HANDLING STRUCTURED DATA, USED FOR DATAFRAMES , DATA CLEANING ,ETCimport pandas as pddata = {"Name": ["Precious", "Faith", "Caleb"], "Age": [20,22,24], "Grade": ["A", "C", "D"]}df = pd.DataFrame(data)print("Dataframe: \n", df)print("Column Age :\n", df["Age"])print("Filtered by Age > 21 : \n", df[df["Age"] > 21])print("Filtered by Age > 21 : \n", df[df["Name"] == "Caleb"])#MATPLOTLIB FOR STATIC , ANIMATED AND INTERACTIVE PLOTSimport matplotlib.pyplot as pltx = np.arange(1,10)print(x)y = x ** 2plt.plot(x,y)plt.xlabel("X Axis")plt.ylabel("Y Axis")plt.title("Line plot of Y = X ^ 2")plt.show()#SEBORN IS BUILT ON MATPLOTLIB AND USED FOR MAINLY STATISTICAL PLOTSimport seaborn as snsdata = pd.DataFrame({"X": np.random.randn(100),                     "Y": np.random.randn(100)})sns.scatterplot(x = "X", y = "Y", data = data)plt.title("Random Plotting")plt.show